import React, { useEffect, useState } from "react";

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/posts") // Spring Boot API Ìò∏Ï∂ú
      .then((response) => response.json()) // JSON Î≥ÄÌôò
      .then((data) => setPosts(data)) // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      .catch((error) => console.error("Error fetching posts:", error));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center p-6">
      <div className="w-full max-w-3xl">
        <h1 className="text-3xl font-bold text-center mb-6 text-gray-800">
          üìú Í≤åÏãúÍ∏Ä Î™©Î°ù
        </h1>

        {posts.length === 0 ? (
          <p className="text-center text-gray-600">Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          <ul className="space-y-6">
            {posts.map((post) => (
              <li
                key={post.postId}
                className="bg-white border rounded-xl p-5 shadow-lg hover:shadow-xl transition duration-300"
              >
                <h2 className="text-2xl font-semibold text-gray-900">{post.title}</h2>
                <p className="text-gray-700 mt-2">{post.content}</p>

                <div className="flex justify-between items-center mt-4">
                  <p className="text-sm text-gray-500">
                    <span role="img" aria-label="like">üëç</span> {post.likeCount} Likes
                  </p>

                  <button className="bg-blue-500 text-white px-4 py-2 rounded-lg text-sm font-medium shadow-md hover:bg-blue-600 transition">
                    ÏûêÏÑ∏Ìûà Î≥¥Í∏∞ ‚Üí
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default PostList;
