import React, { useEffect, useState } from "react";

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/posts") // Spring Boot API Ìò∏Ï∂ú
      .then((response) => response.json()) // JSON Î≥ÄÌôò
      .then((data) => setPosts(data)) // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      .catch((error) => console.error("Error fetching posts:", error));
  }, []);

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Í≤åÏãúÍ∏Ä Î™©Î°ù</h1>
      {posts.length === 0 ? (
        <p>Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul className="space-y-4">
          {posts.map((post) => (
            <li
              key={post.postId}
              className="border p-4 rounded-lg shadow-sm hover:shadow-md transition"
            >
              <h2 className="text-xl font-semibold">{post.title}</h2>
              <p className="text-gray-700">{post.content}</p>
              <p className="text-sm text-gray-500">
                <span role="img" aria-label="like">üëç</span> {post.likeCount} Likes
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default PostList;
