import React, { useEffect, useState } from "react";
import { getPosts, likePost } from "../services/api";

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    const data = await getPosts();
    setPosts(data);
  };

  const handleLike = async (postId) => {
    await likePost(postId);
    fetchPosts(); // Ï¢ãÏïÑÏöî ÌõÑ ÏóÖÎç∞Ïù¥Ìä∏
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Í≤åÏãúÌåê</h1>
      {posts.map((post) => (
        <div key={post.post_id} className="border p-4 my-2 rounded-lg shadow">
          <h2 className="text-xl font-semibold">{post.title}</h2>
          <p>{post.content}</p>
          <div className="flex justify-between items-center mt-2">
            <span>üëç {post.like_count}</span>
            <button
              onClick={() => handleLike(post.post_id)}
              className="bg-blue-500 text-white px-3 py-1 rounded"
            >
              Ï¢ãÏïÑÏöî
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PostList;
