import React, { useEffect, useState } from "react";

const PostList = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/posts") // Spring Boot API Ìò∏Ï∂ú
      .then((response) => response.json()) // JSON Î≥ÄÌôò
      .then((data) => setPosts(data)) // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      .catch((error) => console.error("Error fetching posts:", error));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center p-6">
      <div className="w-full max-w-4xl bg-white shadow-md rounded-lg p-6">
        <h1 className="text-3xl font-bold text-center mb-6 text-gray-800">
          üìú Í≤åÏãúÍ∏Ä Î™©Î°ù
        </h1>

        {posts.length === 0 ? (
          <p className="text-center text-gray-600">Í≤åÏãúÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="table-auto w-full border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-200">
                  <th className="border border-gray-300 px-4 py-2 text-left">Î≤àÌò∏</th>
                  <th className="border border-gray-300 px-4 py-2 text-left">Ï†úÎ™©</th>
                  <th className="border border-gray-300 px-4 py-2 text-left">ÎÇ¥Ïö©</th>
                  <th className="border border-gray-300 px-4 py-2 text-left">Ï¢ãÏïÑÏöî</th>
                </tr>
              </thead>
              <tbody>
                {posts.map((post, index) => (
                  <tr key={post.postId} className="hover:bg-gray-100 transition">
                    <td className="border border-gray-300 px-4 py-2">{index + 1}</td>
                    <td className="border border-gray-300 px-4 py-2 font-semibold">{post.title}</td>
                    <td className="border border-gray-300 px-4 py-2">{post.content}</td>
                    <td className="border border-gray-300 px-4 py-2 text-center">
                      <span role="img" aria-label="like">üëç</span> {post.likeCount}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default PostList;
